		<button type="submit" name="submit">SUBMIT</button>
	</form>

<?php $this->load->view('inc/adsense-h1-title'); ?>

	<h2>Alternate Options For Tracking <?=$vendor->vendor_name;?> Courier</h2>

	<p>You may also track status of consignment from the official website of <?=$vendor->vendor_name;?> courier in <u><b><a href="<?=$vendor->vendor_website;?>" target="_blank"><?=$vendor->vendor_website;?></a></b></u>. Alternatively reach out to customer support team or E-mail ID.</p>

	<?php $this->load->view('inc/adsense-body1'); ?>

    <?php
		if($vendors = vendors( array("category"=>$vendor->category) )){
			$total = 0;
			$items = "";
			foreach ($vendors as $vk => $v) {
				if($vendor->vendor_id != $v->vendor_id){
					$class = ($vk < 30 ? "" : "hide");
					$items .= '<div class="col-md-3 '.$class.'"><a href="'.base_url($category->url."/tracking-".$v->vendor_url).'">'.$v->vendor_name.'</a></div>';
					$total++;
			 	}
		 	}

		 	if($total){
			 	echo '<div class="category-vendors">
			 		<h3>Other Vendors in '.$category->name.' Category</h3>
				 	<div class="vendors-list row">'.$items.'</div>
				 	'.($total > 30 ? '<div class="text-right" style="margin-top: -5px;"><a href="javascript:void(0)" class="show_ext">View More</a></div>' : "").'
				</div>';
			}
		}
	?>
</div>

<script type="text/javascript">
	$(document).on("click", ".show_ext", function(){
		$(".vendors-list .hide").removeClass("hide");
		$(this).remove();
	});
</script>
